// src/pages/AdminLogin.jsx
import React, { useState } from "react";
import { Helmet } from "react-helmet";
import { useNavigate } from "react-router-dom";
import axios from "axios";

import NavBar from "../components/common/navBar";
import Footer from "../components/common/footer";
import Logo from "../components/common/logo";

import INFO from "../data/user";
import SEO from "../data/seo";

import "./styles/adminLogin.css";

const AdminLogin = () => {
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");
	const [error, setError] = useState("");

	const navigate = useNavigate(); // ðŸ‘ˆ Tambahkan ini

	const handleLogin = async (e) => {
		e.preventDefault();
		try {
			const res = await axios.post(
				"http://localhost:5000/api/admin/login",
				{
					email,
					password,
				}
			);
			localStorage.setItem("token", res.data.token);
			setError("");
			navigate("/admin"); // ðŸ‘ˆ Pindah ke dashboard setelah login sukses
		} catch (err) {
			setError("Login gagal. Periksa username atau password.");
		}
	};

	const currentSEO = SEO.find((item) => item.page === "login");

	return (
		<React.Fragment>
			<Helmet>
				<title>{`Admin Login | ${INFO.main.title}`}</title>
				<meta
					name="description"
					content={currentSEO?.description || ""}
				/>
				<meta
					name="keywords"
					content={currentSEO?.keywords?.join(", ") || ""}
				/>
			</Helmet>

			<div className="page-content">
				<NavBar active="login" />
				<div className="content-wrapper login-container">
					<div className="login-logo-container">
						<div className="login-logo">
							<Logo width={46} />
						</div>
					</div>

					<div className="login-form-container">
						<h2>Admin Login</h2>
						<form onSubmit={handleLogin} className="login-form">
							<input
								type="text"
								placeholder="Email"
								value={email}
								onChange={(e) => setEmail(e.target.value)}
								required
							/>
							<input
								type="password"
								placeholder="Password"
								value={password}
								onChange={(e) => setPassword(e.target.value)}
								required
							/>
							<button type="submit">Login</button>
							{error && <p className="error">{error}</p>}
						</form>
					</div>
				</div>
				<Footer />
			</div>
		</React.Fragment>
	);
};

export default AdminLogin;
