// src/pages/SubmitForm.jsx
import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
import axios from "axios";

import NavBar from "../components/common/navBar";
import Footer from "../components/common/footer";
import INFO from "../data/user";
import SEO from "../data/seo";

import "./styles/SubmitForm.css";

const SubmitForm = () => {
	const [formData, setFormData] = useState({
		name: "",
		email: "",
		message: "",
	});
	const [submitted, setSubmitted] = useState(false);

	const currentSEO = SEO.find((item) => item.page === "submit");

	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	const handleChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			await axios.post("http://localhost:5000/api/customers", formData);
			setSubmitted(true);
			setFormData({ name: "", email: "", message: "" });
		} catch (error) {
			console.error("Gagal mengirim pesan", error);
			alert("Terjadi kesalahan saat mengirim pesan.");
		}
	};

	return (
		<>
			<Helmet>
				<title>{`Submit | ${INFO.main.title}`}</title>
				<meta name="description" content={currentSEO?.description} />
				<meta
					name="keywords"
					content={currentSEO?.keywords.join(", ")}
				/>
			</Helmet>

			<div className="page-content">
				<NavBar active="submit" />

				<div className="submit-form-container">
					<h2>Diskusikan Proyek Anda</h2>
					<form onSubmit={handleSubmit}>
						<input
							type="text"
							name="name"
							placeholder="Nama Lengkap"
							value={formData.name}
							onChange={handleChange}
							required
						/>
						<input
							type="email"
							name="email"
							placeholder="Email"
							value={formData.email}
							onChange={handleChange}
							required
						/>
						<textarea
							name="message"
							placeholder="Ceritakan kebutuhan proyek Anda"
							value={formData.message}
							onChange={handleChange}
							required
						/>
						<button type="submit">Kirim Pesan</button>
					</form>

					{submitted && (
						<p
							style={{
								color: "green",
								marginTop: "16px",
								textAlign: "center",
							}}
						>
							Pesan berhasil dikirim. Saya akan segera menghubungi
							Anda.
						</p>
					)}
				</div>

				<Footer />
			</div>
		</>
	);
};

export default SubmitForm;
