import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
import axios from "axios";
import { useNavigate } from "react-router-dom";

import NavBar from "../components/common/navBar";
import Footer from "../components/common/footer";
import Logo from "../components/common/logo";

import INFO from "../data/user";
import SEO from "../data/seo";

import "./styles/submit.css"; // Gunakan CSS yang sama dengan SubmitForm

const AdminLogin = () => {
	const [formData, setFormData] = useState({ email: "", password: "" });
	const [error, setError] = useState("");
	const navigate = useNavigate();

	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	const currentSEO = SEO.find((item) => item.page === "login");

	const handleChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();

		try {
			const res = await axios.post(
				"http://localhost:5000/api/admin/login",
				formData
			);
			localStorage.setItem("token", res.data.token);
			navigate("/admin");
		} catch (err) {
			setError("Login gagal. Periksa email atau password.");
		}
	};

	return (
		<>
			<Helmet>
				<title>{`Login | ${INFO.main.title}`}</title>
				<meta name="description" content={currentSEO?.description} />
				<meta
					name="keywords"
					content={currentSEO?.keywords?.join(", ")}
				/>
			</Helmet>

			<div className="page-content">
				<NavBar active="login" />
				<div className="content-wrapper submit-container">
					<div className="submit-first-area">
						<div className="submit-logo-container">
							<Logo width={46} />
						</div>

						<div className="submit-first-area-left-side">
							<div className="submit-title">Login Admin</div>
							<div className="submit-subtitle">
								Masukkan email dan password untuk masuk ke
								dashboard admin.
							</div>

							<form
								className="submit-form"
								onSubmit={handleSubmit}
							>
								<input
									type="email"
									name="email"
									placeholder="Email"
									value={formData.email}
									onChange={handleChange}
									required
								/>

								<input
									type="password"
									name="password"
									placeholder="Password"
									value={formData.password}
									onChange={handleChange}
									required
								/>

								<button type="submit">Login</button>

								{error && (
									<p
										style={{
											color: "red",
											marginTop: "10px",
										}}
									>
										{error}
									</p>
								)}
							</form>
						</div>
					</div>
				</div>

				<Footer />
			</div>
		</>
	);
};

export default AdminLogin;
