import { useState, useEffect } from "react";
import axios from "axios";
import NavBar from "../components/common/navBar";
import Footer from "../components/common/footer";
import Logo from "../components/common/logo";
import { Helmet } from "react-helmet";
import INFO from "../data/user";
import SEO from "../data/seo";

import "./styles/submit.css";

export default function SubmitForm() {
	const [formData, setFormData] = useState({
		name: "",
		email: "",
		message: "",
	});
	const [submitted, setSubmitted] = useState(false);

	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	const currentSEO = SEO.find((item) => item.page === "contact");

	const handleChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			await axios.post("http://localhost:5000/api/customers", formData);
			setSubmitted(true);
		} catch (error) {
			console.error(error);
		}
	};

	return (
		<div className="page-content">
			<Helmet>
				<title>{`Submit | ${INFO.main.title}`}</title>
				<meta name="description" content={currentSEO.description} />
				<meta
					name="keywords"
					content={currentSEO.keywords.join(", ")}
				/>
			</Helmet>

			<NavBar active="submit" />
			<div className="content-wrapper">
				<div className="submit-logo-container">
					<div className="submit-logo">
						<Logo width={46} />
					</div>
				</div>

				<div className="submit-container">
					<h2 className="submit-title">Hubungi Saya</h2>
					{submitted ? (
						<p className="submit-success">
							Pesan kamu sudah terkirim. Terima kasih!
						</p>
					) : (
						<form onSubmit={handleSubmit} className="submit-form">
							<input
								type="text"
								name="name"
								placeholder="Nama"
								onChange={handleChange}
								required
							/>
							<input
								type="email"
								name="email"
								placeholder="Email"
								onChange={handleChange}
								required
							/>
							<textarea
								name="message"
								placeholder="Pesan"
								onChange={handleChange}
								required
								rows={5}
							/>
							<button type="submit">Kirim</button>
						</form>
					)}
				</div>

				<div className="page-footer">
					<Footer />
				</div>
			</div>
		</div>
	);
}
