// File: src/pages/Login.jsx
import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
import { useNavigate } from "react-router-dom";
import axios from "axios";

import NavBar from "../components/common/navBar";
import Footer from "../components/common/footer";
import Logo from "../components/common/logo";

import INFO from "../data/user";
import SEO from "../data/seo";

import "./styles/login.css";

const Login = () => {
	const [formData, setFormData] = useState({ email: "", password: "" });
	const [error, setError] = useState("");
	const navigate = useNavigate();

	useEffect(() => {
		window.scrollTo(0, 0);
	}, []);

	const currentSEO = SEO.find((item) => item.page === "login");

	const handleChange = (e) => {
		setFormData({ ...formData, [e.target.name]: e.target.value });
	};

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const res = await axios.post(
				"http://localhost:5000/api/admin/login",
				formData
			);
			localStorage.setItem("token", res.data.token);
			navigate("/admin");
		} catch (err) {
			setError("Login gagal. Periksa email atau password.");
		}
	};

	return (
		<React.Fragment>
			<Helmet>
				<title>{`Login | ${INFO.main.title}`}</title>
				<meta
					name="description"
					content={currentSEO?.description || ""}
				/>
				<meta
					name="keywords"
					content={currentSEO?.keywords?.join(", ") || ""}
				/>
			</Helmet>

			<div className="page-content">
				<NavBar active="login" />
				<div className="content-wrapper login-container">
					<div className="login-logo-container">
						<Logo width={46} />
					</div>

					<div className="login-form-wrapper">
						<h2 className="login-title">Login Admin</h2>

						<form onSubmit={handleSubmit} className="login-form">
							<input
								type="text"
								name="email"
								placeholder="Email"
								value={formData.email}
								onChange={handleChange}
								required
							/>

							<input
								type="password"
								name="password"
								placeholder="Password"
								value={formData.password}
								onChange={handleChange}
								required
							/>

							<button type="submit">Login</button>
							{error && <p className="login-error">{error}</p>}
						</form>
					</div>
				</div>

				<Footer />
			</div>
		</React.Fragment>
	);
};

export default Login;
